custom_css:
/*
 * 🌊【黄河】 - 简单配色优化的SillyTavern主题
 * 配色：【日间🌅/蓝黄】☁️
 * 版本：plus 4.3
 * 更改：微调配色
 */

/* ===== 全局：颜色定义 ===== */
:root {
    /* = 基础颜色 = */
    /* 输入框 */
    --black30a: rgba(245, 245, 231, 0.85);
    /* 小代码块背景+设置栏左 */
    --black70a: rgba(100, 149, 237, 0.5);
    /* 设置栏中 */
    --grey30a: rgba(255, 254, 213, 0.3); 
    /* 滚动条 */
    --grey7070a: rgba(245, 245, 231, 0.85);

    /* = 预设颜色 = */
    /* 预设字体+设置栏右 */
    --white30a: rgba(111, 153, 193, 0.6);
    /* 预设图标+上字+滑块条 */
    --white50a: rgba(111, 153, 193, 0.6);
}

/* ====== 编辑修改框（正文+推理块） ====== */
.edit_textarea, .mes_reasoning_details textarea {
    background-color: rgba(255, 255, 255, 0.5);
    color: rgba(53, 77, 105, 0.8);
    border: 2px solid rgba(70, 84, 114, 0.5);
}

/* ====== 大代码块 ====== */
/* 大代码块 - 覆盖.hljs的背景颜色和文字颜色 */
.hljs {
    background-color: transparent;
    color: inherit;
}

/* 大代码块 - 颜色配置 */
pre {
    font-family: monospace;
    background-color: rgba(70, 84, 114, 0.9);
    color: rgba(193, 216, 226, 0.9);
    padding: 6px;
    border-radius: 10px;
    text-shadow: 0 0 5px rgba(245, 245, 231, 0.3);
}

/* 阴影继承 */
pre * {
    text-shadow: inherit;
}

/* 小代码块 */
code {
    color: rgba(255, 255, 255, 0.8);
    background-color: var(--black70a);
}

/* ====== 状态栏限高 ====== */
/* 代码块滚动条 */
.bubblechat .mes_text pre:has(code) {
    /* ▶ 压缩大小（在此修改状态栏限高） */
    max-height: 220px;
    overflow-y: auto;
}

/* 滚动条轨道 */
.bubblechat .mes_text pre:has(code)::-webkit-scrollbar-track {
    background-color: rgba(245, 245, 230, 0.4);
}

/* 滚动条滑块 */
.bubblechat .mes_text pre:has(code)::-webkit-scrollbar-thumb {
    background-color: var(grey7070a);
    border-radius: 10px;
}

/* ====== 推理块 think ====== */
/* 推理块 - 头部美化 */
.mes_reasoning_header {
    background: linear-gradient(135deg, rgba(100, 149, 237, 0.4) 30%, rgba(211, 226, 222, 1) 70%);
    border: 2px solid rgba(111, 153, 198, 0.9);
    border-radius: 10px;
    color: rgba(245, 245, 231, 1);
    font-weight: bold;
}
